import React, { Component } from 'react';
import fetch from 'isomorphic-unfetch';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import Popover from '@material-ui/core/Popover';
import { makeStyles } from '@material-ui/core/styles';
import { ReactSVG } from 'react-svg';
import  { useState } from 'react';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
//My Components
import events from '../assets/events.js'
import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import Dailies from '../components/Dailies.js'
import IconSvg from '../assets/img/score_icon.svg';
import { purple } from '@material-ui/core/colors';
import Switch from '@material-ui/core/Switch';
import eventStyles from '../assets/css/custEvents.module.css'
//My Styles
import styles from '../assets/css/main.module.css'
import { Hidden } from '@material-ui/core';
import calStyles from '../assets/css/rbc.module.css';
const localizer = momentLocalizer(moment);



const useStyles = makeStyles(theme => ({
  popover: {
    pointerEvents: 'none',
  },
  paper: {
    width: '235px', //Width of MAL images
    height:'430px',
    position: "absolute",
    overflow: "hidden",
    padding: theme.spacing(0.6),
    pointerEvents: 'auto'
  },
}))

const MyCalendar = props => {
  
  
  function getCustomToolbar(toolbar) {



    const goToBack = () => {
      let mDate = toolbar.date;
      let newDate = new Date(
        mDate.getFullYear(),
        mDate.getMonth() - 1,
        1);
      toolbar.onNavigate('prev', newDate);


    }
    const goToNext = () => {
      let mDate = toolbar.date;
      let newDate = new Date(
        mDate.getFullYear(),
        mDate.getMonth() + 1,
        1);
      toolbar.onNavigate('next', newDate);


    }
    const goToCurrent = () => {
      const now = new Date();
      toolbar.date.setMonth(now.getMonth());
      toolbar.date.setYear(now.getFullYear());
      toolbar.onNavigate('current');
    };

    const label = () => {
      const date = moment(toolbar.date);
      return (
        <span><b>{date.format('MMMM')}</b><span> {date.format('YYYY')}</span></span>
      );
    };

    return (
      <>
        <div className=" text-center align-center">
          <div className="rbc-toolbar">
            <div className="rbc-btn-group">
              <button type="button" onClick={goToBack}>
                <span className="prev-icon">&#8249;</span>
              </button>
              <button type="button" onClick={goToCurrent}>
                <span className="next-icon">Today</span>
              </button>
              <button type="button" onClick={goToNext}>
                <span className="next-icon">&#8250;</span>
              </button>
            </div>
          </div >
          <div><label>{label()}</label></div>
          <br/>
        </div>
      </>
    )
  };
  //Move this into 'components'?
  function EventPop(event) {

    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handlePopoverOpen = event => {
      setAnchorEl(event.currentTarget);
    };

    const handlePopoverClose = () => {
      setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
    
    return (
      <>
        <div>
          <div
            onMouseEnter={handlePopoverOpen}
            onMouseLeave={handlePopoverClose}
            aria-controls="simple-menu"
            aria-haspopup={true}
          >
            {event.title}

          </div>
          <Popover

            className={classes.popover}
            classes={{
              paper: classes.paper,
            }}
            open={open}
            anchorEl={anchorEl}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            transformOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}

            disableRestoreFocus
            // onClick={handlePopoverClose}
            // onMouseLeave={handlePopoverClose}
            PaperProps={{ onMouseEnter: handlePopoverOpen, onMouseLeave: handlePopoverClose }}
          >

            <div>
              <div>
                <a href={event.event.url} target="_blank">{event.title}</a>
              </div>
              <div>
                <IconSvg />
                {event.event.score}

              </div>
              <br />
              <div>
                
                <div className={styles.bind}>
                  
                   <img src={event.event.image} alt={event.title} className={styles.blur}/>
                   <div className={styles.screen}></div>
                 
                
                  <div className={styles.description}>
                    {event.event.desc}
                  </div>
                </div>
              </div>
            



            </div>

          </Popover>
        </div>
      </>
    )


  };
  function EventAgenda({ event }) {
    return (
      <span>
        <em style={{ color: 'magenta' }}>{event.title}</em>
        
      </span>
    )
  }
  const [state, setState] = useState({isDark: false});
  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };
  return (
    <>

      <div className={`${styles.bgc} ${state.isDark ? styles.light : null}`}>
        <Header isDark={state.isDark} className="top-bar" />
        <br/>    
        <div className="grid-x text-center align-center">
          <div>
            <h4 style={{fontFamily:'Dosis'}}>Released Today</h4>

            {/* <Dailies /> */}
          </div>
        </div>
        <br />
        <br />
        <div className={"grid-container"}>
          <Calendar
            
            style={{ height: 650 }}

            tooltipAccessor={null}
            popup
            localizer={localizer}
            // events={props.animeArr}
            events={events}
            startAccessor="start"
            endAccessor="end"

            defaultDate={moment().toDate()}
            eventPropGetter={
              (event) =>{
                let newStyle= {backgroundColor: 'rgba(31, 125, 247, 0.7)'};

                if(state.isDark){
                  newStyle.backgroundColor = 'rgba(175, 16, 43, 0.89)'
                }

                return(
                  newStyle
                );
              }
            }
            components={{
              event: EventPop,
              //event: EventAgenda,
              toolbar: getCustomToolbar
            }}

          />
          
        </div>
        <br />
        <br />
        <Switch
          checked={state.isDark}
          onChange={handleChange}
          name="isDark"
          inputProps={{ 'aria-label': 'secondary checkbox' }}
        />
        <Footer />

      </div>

    </>
  );
}

// MyCalendar.getInitialProps = async function () {
  //const res = await fetch('https://api.jikan.moe/v3/season');
  //const data = await res.json();

  // if (data) {
  //   var animeArr = data.anime.map(anime => ({
  //     title: anime.title,
  //     url: anime.url,
  //     desc: anime.synopsis,
  //     start: anime.airing_start,
  //     end: anime.airing_start,
  //     image: anime.image_url,
  //     score: anime.score
  //   }

  //   ));
  //   return {
  //     relData: data.anime,
  //     animeArr
  
  //   };
  // }
  // else{
  //   return <div>Loading</div>;
  // }
  // console.log(data.anime[0].score);
  // console.log(data.anime[0].title);

  
// };





export default MyCalendar;
