import React, { Component } from 'react';
//import Layout from '../components/MyLayout.js';
//import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import Popover from '@material-ui/core/Popover';
//import MyCalendar from '../components/Calendar.js'
import MouseOverPopover from '../components/popover.js'
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
//import { Popover } from '@material-ui/core';
import Button from '@material-ui/core/Button';
const localizer = momentLocalizer(moment);

const useStyles = makeStyles(theme => ({
  
  paper: {
    padding: theme.spacing(1),
  },
}));




// const Event = event =>{
//   <span>
//     <strong>{event.title}</strong>
//   </span>
// }
// const EventAgenda = event=>{
//   <span>
//      <em style={{ color: 'magenta' }}>{event.title}</em>
//     <p>{event.desc}</p>
//   </span>
// }
const MyCalendar = props => {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handlePopoverOpen = event => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };
  function Event({ event }) {
    return (
      <Typography
        aria-owns={open ? event.title : undefined}
        aria-haspopup="true"
        onMouseEnter={handlePopoverOpen}
        onMouseLeave={handlePopoverClose}
        component={'span'}
      >
        {event.title}
        <Popover
          id={event.title}
          className={classes.popover}
          classes={{
            paper: classes.paper,
          }}
          open={open}
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'left',
          }}
          onClose={handlePopoverClose}
          disableRestoreFocus
        >
          <Typography>{event.title}</Typography>
        </Popover>

      </Typography>
    )
  }
  const open = Boolean(anchorEl);
  
  return (
    <>
      <div>
      
          
        <Calendar

          popup
          localizer={localizer}
          events={props.animeArr}
          startAccessor="start"
          endAccessor="end"
          onSelectEvent={event =>
            <>
            <Typography
            aria-owns={open ? 'mouse-over-popovers' : undefined}
            aria-haspopup="true"
            onClick={handlePopoverOpen}
            //onMouseLeave={handlePopoverClose}
            
          >
            {event.title}
            </Typography>
            <Popover
              id="mouse-over-popovers"
              className={classes.popover}
              classes={{
                paper: classes.paper,
              }}
              open={open}
              anchorEl={anchorEl}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              onClose={handlePopoverClose}
              disableRestoreFocus
            >
              <Typography>{event.title}</Typography>
            </Popover>
            </>
          }
          defaultDate={moment().toDate()}
          style={{ height: 500 }}
          //components = {{event: Event} }
        />


        <Typography
          aria-owns={open ? 'mouse-over-popover' : undefined}
          aria-haspopup="true"
          onClick={handlePopoverOpen}
          //onMouseLeave={handlePopoverClose}
          component={'span'}
        >
          Hello
      </Typography>
        <Popover
          id="mouse-over-popover"
          className={classes.popover}
          classes={{
            paper: classes.paper,
          }}
          open={open}
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'left',
          }}
          onClose={handlePopoverClose}
          disableRestoreFocus
        >
          I use Popover.
        </Popover>
        <MouseOverPopover />
      </div>

    </>
  );
}

MyCalendar.getInitialProps = async function () {
  const res = await fetch('https://api.jikan.moe/v3/season/');
  const data = await res.json();


  var animeArr = data.anime.map(anime => ({
    title: anime.title,
    start: anime.airing_start,
    end: anime.airing_start
  }

  ));

  console.log(`${animeArr[0].title}`);
  return {
    animeArr
  };
};





export default MyCalendar;
