import fetch from 'isomorphic-unfetch';

const Dailies = props => {
    return(
    <div>
        <h3>{props.dailyArr[0].title}</h3>
    </div>
    )

}

Dailies.getInitialProps = async function () {

    const res = await fetch('https://api.jikan.moe/v3/season/');
    const data = await res.json();
    var today = new Date();
    var dailyArr 
    for (var i = 0; i < data.anime.length; i++) {
        if (data.anime[i].airing_start == today) {
            dailyArr = data.anime.map(anime => ({


                title: anime.title,
                date: anime.airing_start,
                image: anime.image_url
            }));
        }
    }

    //console.log(`${dailyArr[0].title}`);

    console.log(data.anime[0].title);
    return { dailyArr }
}
export default Dailies;
