    //   return {
    //     anilist: data.anime,
    //     seasonName: data.season_name,
    //     seasonYear: data.season_year
    //   }
    // }

  }

  async componentDidUpdate() {
    if (this.state.selectValue !== '') {


      await fetch(`https://api.jikan.moe/v3/season/2019/${this.state.selectValue}`)
      .then(data => data.json())
      .then(data => this.setState({data}));

    

      // return {
      //   anilist: data.anime,
      //   seasonName: data.season_name,
      //   seasonYear: data.season_year
      // }
    }
  }
  // static async componentDidMount() {

  //   if (this.state == null) {

  //     await fetch(`https://api.jikan.moe/v3/season/2020/spring`)
  //     .then(data => data.json())
  //     .then(data => this.setState({data}));
      
      
  //     console.log(`Show data fetched. Count: ${data.anime.length}`);
  //     console.log(`Season: ${data.season_name}`);


  //     // return {
  //     //   anilist: data.anime,
  //     //   seasonName: data.season_name,
  //     //   seasonYear: data.season_year
  //     // };
  //   }
  // }
  render() {
    //console.log(this.state)
    const {data} = this.state
    return (
      
      <React.Fragment>
        <select value={this.state.selectValue} onChange={this.handleChange}>
          <option value="winter">Winter</option>
          <option value="spring">Spring</option>
          <option value="summer">Summer</option>
          <option value="fall">Fall</option>
        </select>
        <h1>{data.anime.season_name} {data.anime.season_year}</h1>
        
        <ul>
          {console.log(data.anime)}
          {data.anime.map(anime => {
            return (
          <li key={anime.mal_id}>
            <Link href="/p/[id]" as={`/p/${anime.mal_id}`}>
              <a>{anime.title}</a>
            </Link>
          </li>
            )
            })}
        </ul>
        </React.Fragment>
          
        
      
    )
  }
}
