import React, { useState, useEffect } from "react";
import fetch from 'isomorphic-unfetch';

const Dailies = () => {
    const [hasError, setErrors] = useState(false);
    const [data, setData] = useState({anime: []});
    const today = new Date();
    async function fetchData() {
        const res = await fetch('https://api.jikan.moe/v3/season/2020/spring');
        res
            .json()
            .then(res => setData(res))
            .catch(err => setErrors(err));

        
    }
    useEffect(() => {
        fetchData();
        
    },[]);
    const dataArr = data.anime.map(anime => ({
        title: anime.title
    }));
    console.log(dataArr);
    return('hello');
    for (var i = 0; i < data.anime.length; i++) {
        if (data.anime[i].airing_start == today) {
            return (

                <div>
                    {/* <p>{dataArr[0].title}</p> */}
                    <ul>
                        {data.anime[i].map(anime => (

                            <li key={anime.mal_id}>
                                <a href={anime.url}>{anime.title}</a>
                            </li>

                        ))}
                    </ul>
                </div>
            );
        }
    }
    
}
