import React, { useState, useEffect } from "react";
import fetch from 'isomorphic-unfetch';
const Dailies = () => {
    const [hasError, setErrors] = useState(false);
    const [data, setData] = useState({dailies: []});

    async function fetchData() {
        const res = await fetch('https://api.jikan.moe/v3/season/2020/spring');
        res
            .json()
            .then(res => setData(res))
            .catch(err => setErrors(err));

        const dataArr = dailies.anime.map(anime => ({
            title: anime.title
        }));
    }
    useEffect(() => {
        fetchData();
        
    },[]);
    
    return (
        <div>
            <span>{dataArr.title}</span>
            <hr />
            <span>Error: {JSON.stringify(hasError)}</span>
        </div>
    );
}
