import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
//import event from './event'
import fetch from 'isomorphic-unfetch';
import React, { Component } from 'react';
const localizer = momentLocalizer(moment)

export default class MyCalendar extends Component {
  constructor(){
    super();
  this.state =
    {
      seasonValue: 'winter',
      yearValue: new Date().getFullYear(),

      data: { anime: [] },
      
    };
    
  }
  async componentDidMount() {

    // const res = await fetch(`https://api.jikan.moe/v3/season/2020/winter`);
    // const data = await res.json();

    await fetch(`https://api.jikan.moe/v3/season/2020/winter`)
    .then(data => data.json())
    .then(data => this.setState({data}))
    .catch(error => console.log(error));

    //console.log(data.anime[0]);
    console.log(this.state.data.anime[1].airing_start);
    //return { animes: data.map(item => item.anime) };
    
  }
  render() {
    const {data} = this.state;
    
    let title = data.season_name;
    return (
      <>
        <div>
          
          <Calendar
            localizer={localizer}
            // events={[{

            
            //   'title':'Test',
            //   'start': new Date(),
            //   'end': new Date()
              
            // }]}
            
            events={[{

              title: title,
              
              //'allDay': true,
              start: new Date(),
              end: new Date()

            }]}
            // events={data.anime.map(anime =>
            //   [{
            //     key: anime.mal_id,
            //     title: anime.title,
            //     start: anime.airing_start,
            //     end: anime.airing_start
            //   }]
            // )}
            
            startAccessor="start"
            endAccessor="end"
            defaultDate={moment().toDate()}
            style={{ height: 500 }}
             
          />
           
        </div>
      </>
    )
  }


}
