import fetch from 'isomorphic-unfetch';

const Dailies = props => {
    <div>
        <h3>hi</h3>
    </div>

}

Dailies.getInitialProps = async function(){
    
    const res = await fetch('https://api.jikan.moe/v3/season/');
    const data = await res.json();
    var today = new Date();
    var dailyArr;
    if(data.anime.airing_start == today)
    {
        dailyArr = data.anime.map(anime => ({
        
        
            title: anime.title,
            date: anime.airing_start,
            image: anime.image_url
        }));
    }
   
    console.log(`${dailyArr[0].title}`);
            
    
    return {dailyArr}
}
export default Dailies;
