import React, { useState, useEffect } from "react";
import fetch from 'isomorphic-unfetch';
import moment from 'moment'
import styles from '../assets/css/dailies.module.css'

const Dailies = () => {
    const [hasError, setErrors] = useState(false);
    const [data, setData] = useState({ anime: [] });
    const today = new Date();

    var dailyArr;
    async function fetchData() {
        const res = await fetch('https://api.jikan.moe/v3/season');
        res
            .json()
            .then(res => setData(res))
            .catch(err => setErrors(err));

    }

    useEffect(() => {
        fetchData();

    }, []);

    function formatDate(date) {
        var newDate = new moment(date);
        return newDate.format('MM/DD/YYYY');
    }

    dailyArr = data.anime.filter(anime => {
        if (formatDate(anime.airing_start) !== formatDate(today)) {
            return false;
        }
        return true;
    }).map(anime => { return anime });

    if (dailyArr.length == 0) {
        return <p>No anime released today :(</p>
    }

    console.log(dailyArr);

    return (

        <div>
            {/* <p>{dataArr[0].title}</ p> */}
            <ul className={styles.noBullet}>
                {dailyArr.map(anime => (

                    <li key={anime.mal_id}>
                        <div className="card" style={{width: 225 + 'px'}}>
                            
                            <div className="card-divider">
                                <a href={anime.url}>{anime.title}</a>
                            </div>
                            <img src={anime.image_url} />
                        </div>
                    </li>

                ))}
            </ul>
        </div>
    );


}

export default Dailies;
