import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import event from './event'
import fetch from 'isomorphic-unfetch';
import React, {Component} from 'react';
const localizer = momentLocalizer(moment)

export default class MyCalendar extends Component{
  render(){

  return(
    <>
  <div>
    <Calendar
      localizer={localizer}
      events={[{

        'title': props.animeTitle,
        //'allDay': true,
        'start': new Date(),
        'end': new Date()

    }]}
      startAccessor="start"
      endAccessor="end"
      defaultDate={moment().toDate()}
      style={{ height: 500 }}
      
    />
  </div>
    </>
  )}
getInitialProps =  async function () {

  const res = await fetch(`https://api.jikan.moe/v3/season/2020/winter`);
  const data = await res.json();

  // await fetch(`https://api.jikan.moe/v3/season/2020/spring`)
  // .then(data => data.json())
   

  console.log(`${data.anime.length}`);
  return{animeTitle: data.anime.title};
}

}
