import React, { Component } from 'react';
import Layout from '../components/MyLayout.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import Popover from '@material-ui/core/Popover';
//import MyCalendar from '../components/Calendar.js'
import MouseOverPopover from '../components/popover.js'
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
//import { Popover } from '@material-ui/core';
const localizer = momentLocalizer(moment);

// const useStyles = makeStyles(theme => ({
//   popover: {
//     pointerEvents: 'none',
//   },
//   paper: {
//     padding: theme.spacing(1),
//   },
// }));

function mousePop(){
 
}
const MyCalendar = props => (

  <>
    <div>
      
      <Calendar

        popup
        localizer={localizer}
        events={props.animeArr}
        startAccessor="start"
        endAccessor="end"
        onSelectEvent={event => alert(event.title)}
        defaultDate={moment().toDate()}
        style={{ height: 500 }}
      />
      
      
      <Typography
        aria-owns={props.open ? 'mouse-over-popover' : undefined}
        aria-haspopup="true"
        onMouseEnter={props.handlePopoverOpen}
        onMouseLeave={props.handlePopoverClose}
      >
        Hover with a Popover.
      </Typography>
      <Popover
        id="mouse-over-popover"
        // className={props.classes.popover}
        // classes={{
        //   paper: classes.paper,
        // }}
        open={props.open}
        anchorEl={props.anchorEl}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
        onClose={props.handlePopoverClose}
        disableRestoreFocus
      >
        <Typography>I use Popover.</Typography>
      </Popover>
    </div>

  </>
);

MyCalendar.getInitialProps = async function () {
  const res = await fetch('https://api.jikan.moe/v3/season/');
  const data = await res.json();
  

  var animeArr = data.anime.map(anime => ({
    title: anime.title,
    start: anime.airing_start,
    end: anime.airing_start
  }

  ));

  console.log(`${animeArr[0].title}`);
  return {
    animeArr
  };
};

MyCalendar.mousePop = function () {
  
   //const classes = useStyles();
   const [anchorEl, setAnchorEl] = React.useState(null);

   const handlePopoverOpen = event => {
     setAnchorEl(event.currentTarget);
   };
   const handlePopoverClose = () => {
     setAnchorEl(null);
   };
 
   const open = Boolean(anchorEl);
 
   return{
     //classes,
     handlePopoverOpen,
     handlePopoverClose,
     open
   }
}



export default MyCalendar;
