import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
//import event from './event'
import fetch from 'isomorphic-unfetch';
import React, { Component } from 'react';
const localizer = momentLocalizer(moment)

export default class MyCalendar extends Component {
  constructor(){
    super();
  this.state =
    {
      seasonValue: 'winter',
      yearValue: new Date().getFullYear(),
      data: { anime: [] },
      
    };
    
  }
  async componentDidMount() {

    // const res = await fetch(`https://api.jikan.moe/v3/season/2020/winter`);
    // const data = await res.json();

    await fetch(`https://api.jikan.moe/v3/season/2020/winter`)
    .then(data => data.json())
    .then(data => this.setState({data}))
    .catch(error => console.log(error));

    
    var animeArr = this.state.data.anime;
    //console.log(data.anime);
    //console.log(this.state.data.anime[1].airing_start);
    return{animeArr};
    
  }
  render() {
    const {data} = this.state;
    
    console.log(animeArr);
    //let title = this.props.animeArr.title;
    return (
      <>
        <div>
          
          <Calendar
          
            localizer={localizer}
            // events={[{

            
            //   'title':'Test',
            //   'start': new Date(),
            //   'end': new Date()
              
            // }]}
            
            // events={this.props.aniList.map(anime =>([{
            //   title: anime.title,
            //   start: anime.airing_start,
            //   end: anime.airing_start

            // }]))}
            events={data.anime.map(anime =>{
              [{
                'key': anime.mal_id,
                'title': anime.title,
                'start': anime.airing_start,
                'end': anime.airing_start
              }]
            })}
            
            startAccessor="start"
            endAccessor="end"
            defaultDate={moment().toDate()}
            style={{ height: 500 }}
             
          />
           
        </div>
      </>
    )
  }


}
