import React, { Component } from 'react';
//import Layout from '../components/MyLayout.js';
//import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import Popover from '@material-ui/core/Popover';
//import MyCalendar from '../components/Calendar.js'
import MouseOverPopover from '../components/popover.js'
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
//import { Popover } from '@material-ui/core';
import Button from '@material-ui/core/Button';
const localizer = momentLocalizer(moment);

const useStyles = makeStyles(theme => ({
  popover: {
    pointerEvents: 'none',
  },
  paper: {
    padding: theme.spacing(1),
    pointerEvents: 'auto'
  },
}))

const MyCalendar = props => {
  function getCustomToolbar(toolbar)
  {
    

    const goToMonthView = () => {
      toolbar.onViewChange('month');
      
        setTimeout(() => {
          this.setOffRangeDateStyle();
        }, 100)
      
      }
    const goToBack = () => {
      let mDate = toolbar.date;
      let newDate = new Date(
        mDate.getFullYear(),
        mDate.getMonth() - 1,
        1);
      toolbar.onNavigate('prev', newDate);
      this.getCalendarEvents(newDate);
    
    }
    const goToNext = () => {
      let mDate = toolbar.date;
      let newDate = new Date(
        mDate.getFullYear(),
        mDate.getMonth() + 1,
        1);
      toolbar.onNavigate('next', newDate);
      this.getCalendarEvents(newDate);
    
    }
    return(
    <div className="toolbar-container">
    <div className="navigation-buttons">
      <button className="btn btn-back" onClick={goToBack}>
        Back
      </button>
      <button className="btn btn-next" onClick={goToNext}>
        Next
      </button>
      
    </div>
    <div className="filter-container">
      
      <button className="bg-filter-off" onClick={goToMonthView}><span className="label-filter-off">Month</span></button>
    </div>
  </div >
    )
  };
  function EventPop(event) {

    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handlePopoverOpen = event => {
      setAnchorEl(event.currentTarget);

    };

    const handlePopoverClose = () => {
      setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
    return (
      <>
        <div>
        <div
          onMouseEnter={handlePopoverOpen}
          
          aria-controls="simple-menu" 
          aria-haspopup={true}
        >
          {event.title}
          
        </div>
        <Popover
            id='simple-menu'
            className={classes.popover}
            classes={{
              paper: classes.paper,
            }}
            open={open}
            anchorEl={anchorEl}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            transformOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}

            disableRestoreFocus
            
            onMouseLeave={handlePopoverClose}
          >
            <div>
              
              
              {event.title}
              <br />
              <img src={event.event.image} />
            </div>
          </Popover>
            </div>
      </>
    )


  };
  return (
    <>
      <div>


        <Calendar
          style={{height: 600}}
          
          tooltipAccessor={null}
          popup
          localizer={localizer}
          events={props.animeArr}
          startAccessor="start"
          endAccessor="end"
          
          defaultDate={moment().toDate()}
          
          components={{ 
            event: EventPop,
            toolbar: getCustomToolbar }}

        />

      
      </div>
      
    </>
  );
}

MyCalendar.getInitialProps = async function () {
  const res = await fetch('https://api.jikan.moe/v3/season/');
  const data = await res.json();

  //Make Date Object work with event
  var animeArr = data.anime.map(anime => ({
    title: anime.title,
    start: anime.airing_start,
    end: anime.airing_start,
    image: anime.image_url
  }

  ));
  console.log(`${animeArr[0].image}`);
  console.log(`${animeArr[0].title}`);
  return {
    animeArr

  };
};





export default MyCalendar;
